import { ADD_ITEM, TOGGLE_CART_DROPDOWN } from "./typeActions";

export const toggleCartHidden = () => ({
  type: TOGGLE_CART_DROPDOWN,
});

export const addItem = (item) => ({
  type: ADD_ITEM,
  payload: item,
});
