import {
  ADD_ITEM,
  CHANGE_QUANTITY,
  CLEAR_ITEM_FROM_CART,
  TOGGLE_CART_DROPDOWN,
} from "../actions/typeActions";
import {
  addItemToCart,
  changeQuantityItemFromCart,
  clearAllItemFromCart,
} from "../utils/cartUtils";

const initialState = {
  hidden: true,
  cartItems: [],
};

const cartReducer = (state = initialState, action) => {
  switch (action.type) {
    case TOGGLE_CART_DROPDOWN:
      return { ...state, hidden: !state.hidden };
    case ADD_ITEM:
      return {
        ...state,
        cartItems: [
          ...state.cartItems.addItemToCart(state.cartItems, action.payload),
        ],
      };

    case CHANGE_QUANTITY:
      let cartUpdate = [...state.cartItems];
      cartUpdate = changeQuantityItemFromCart(
        cartUpdate,
        action.item,
        action.add
      );
      // let index = cartUpdate.findIndex(
      //   (cartItem) => cartItem.id === action.item.id
      // );
      // if (index !== -1) {
      //   if (action.add) {
      //     cartUpdate[index].quantity += 1;
      //   } else {
      //     if (cartUpdate[index] > 1) {
      //       cartUpdate[index].quantity -= 1;
      //     } else {
      //       alert("no way");
      //     }
      //   }
      // }
      // state.cartItems = cartUpdate;
      return { ...state };

    case CLEAR_ITEM_FROM_CART:
      return {
        ...state,
        cartItems: clearAllItemFromCart(state.cartItems, action.payload),
      };
    default:
      return state;
  }
};

export default cartReducer;
