import { createSelector } from "reselect";
import { selectCurrentUser } from "./userSelector";

const selectOrder = (state) => state.order;

export const purchaseLoading = createSelector(
  selectOrder,
  (order) => order.loading
);

export const orderLoading = createSelector(
  selectOrder,
  (order) => order.orderLoading
);

export const fetchOrdersByUserId = createSelector(
  selectOrder,
  selectCurrentUser,
  (order, userId) => order.orders.filter((order) => order.userId === userId)
);

export const fetchOrdersNonUserId = createSelector(selectOrder, (order) =>
  order.orders.filter((order) => !order.userId)
);
