import { FETCH_COLLECTIONS_START } from "../actions/typeActions";

const initialState = {
  collections: null,
  isFetching: false,
  errorMessage: null,
};

const shopReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_COLLECTIONS_START:
      return { ...state, isFetching: true };
    default:
      return state;
  }
};

export default shopReducer;
