import axios from "axios";
import { clearItemFromCart } from "./cartAction";
import { PURCHASE_FAIL, PURCHASE_START, PURCHASE_SUCCESS } from "./typeActions";

const purchaseStart = () => {
  return {
    type: PURCHASE_START,
  };
};

const purchaseSuccess = () => {
  return {
    type: PURCHASE_SUCCESS,
  };
};

const purchaseFailed = () => {
  return {
    type: PURCHASE_FAIL,
  };
};

export const purchase = (order, history) => {
  return (dispatch) => {
    dispatch(purchaseStart());
    axios
      .post(
        "https://react-my-burger-4a0ef-default-rtdb.firebaseio.com/orders.json",
        order
      )
      .then((res) => {
        history.push("/");
        dispatch(purchaseSuccess());
        dispatch(clearItemFromCart());
      })
      .catch((err) => {
        dispatch(purchaseFailed());
      });
  };
};
