import {
  PURCHASE_FAIL,
  PURCHASE_START,
  PURCHASE_SUCCESS,
} from "../actions/typeActions";

const initialState = {
  orders: [],
  loading: false,
};

const orderReducer = (state = initialState, action) => {
  switch (action.type) {
    case PURCHASE_START:
      return { ...state, loading: true };
    case PURCHASE_SUCCESS:
      return { ...state, loading: false };
    case PURCHASE_FAIL:
      return { ...state, loading: false };
    default:
      return state;
  }
};
export default orderReducer;
