import { CardElement, useElements, useStripe } from '@stripe/react-stripe-js'
import React, { useEffect, useState } from 'react'
import axios from "../../axios"
import { connect } from 'react-redux'
import CheckoutItem from '../../components/checkout-item/CheckoutItem'
import { selectCartItems, selectCartTotal } from '../../redux/selectors/cartSelector'
import "./CheckoutPage.scss"
import { useHistory } from 'react-router-dom'
import { emptyBasket } from '../../redux/actions/cartAction'

function CheckoutPage({ cartItems, total, dispatch }) {
    const stripe = useStripe();
    const elements = useElements();

    const [error, setError] = useState(null)
    const [disabled, setDisabled] = useState(true)
    const [processing, setProcessing] = useState("")
    const [succeeded, setSucceeded] = useState(false)
    const [clientSecret, setClientSecret] = useState(true)
    const history = useHistory()

    useEffect(() => {
        const getClientSecret = async () => {
            const response = await axios({
                method: "POST",
                url: `/payments/create?total=${total * 100}`
            })
            setClientSecret(response.data.clientSecret)
        }
        getClientSecret()
    }, [cartItems])

    // console.log("ClientL", clientSecret);

    const handleSubmit = async (e) => {
        e.preventDefault()
        setProcessing(true);
        const payload = await stripe.confirmCardPayment(clientSecret, {
            payment_method: {
                card: elements.getElement(CardElement)
            }
        }).then(({ paymentIntent }) => {
            setSucceeded(true);
            setError(null)
            setProcessing(false)
            dispatch(emptyBasket())
            history.replace("/")
        })
    }

    const handleChange = e => {
        setDisabled(e.empty)
        setError(e.error ? e.error.message : "")
    }
    return (
        <div className="checkout-page">
            <div className="checkout-header">
                <div className="header-block">
                    <span>Product</span>
                </div>
                <div className="header-block">
                    <span>Description</span>
                </div>
                <div className="header-block">
                    <span>Quantity</span>
                </div>
                <div className="header-block">
                    <span>Price</span>
                </div>
                <div className="header-block">
                    <span>Remove</span>
                </div>
            </div>
            {
                cartItems.map(cartItem => <CheckoutItem key={cartItem.id} cartItem={cartItem} />)
            }
            <div className="total">
                <span>TOTAL: ${total}</span>
            </div>

            <div className="payment__section">
                <div className="payment__title">
                    <h3>Payment Method:</h3>
                </div>
                <div className="payment__details">
                    <form onSubmit={handleSubmit}>
                        <CardElement onChange={handleChange} />
                        <button disabled={disabled || processing || succeeded || error}>Buy now</button>
                        {error && <div>{error}</div>}
                    </form>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        cartItems: selectCartItems(state),
        total: selectCartTotal(state)
    }
}

export default connect(mapStateToProps)(CheckoutPage)
