import { AUTH_START, AUTH_SUCCESS } from "../actions/typeActions";

const initialState = {
  email: null,
  token: null,
  userId: null,
  loading: false,
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case AUTH_START:
      return { ...state, loading: true };
    case AUTH_SUCCESS:
    default:
      return state;
  }
};

export default userReducer;
