export const addItemToCart = (cartItems, cartItemToAdd) => {
  // console.log("cartItems:", cartItems);
  // console.log("cartItemToAdd:", cartItemToAdd);

  const cartUpdate = [...cartItems];

  let index = cartUpdate.findIndex(
    (cartItem) => cartItem.id === cartItemToAdd.id
  );
  if (index === -1) {
    return [...cartUpdate, { ...cartItemToAdd, quantity: 1 }];
  } else {
    cartUpdate[index] = {
      ...cartUpdate[index],
      quantity: cartUpdate[index].quantity + 1,
    };
    return [...cartUpdate];
  }
};

export const removeItemFromCart = (cartItems, itemToRemove) => {};

export const clearAllItemFromCart = (cartItems, cartItemToClear) => {
  return cartItems.filter((cartItem) => cartItem.id !== cartItemToClear.id);
};
